  const inputRecipeName = useRef();
  const inputChefName = useRef();
  const inputDetails = useRef();
  return (
    <div className="main">
      <input
        className="search"
        type="text"
        placeholder="Enter Recipe Details"
        ref={inputDetails}
      />
      <input
        className="search"
        type="text"
        placeholder="Enter Chef Name"
        ref={inputChefName}
      />
      <input
        className="search"
        type="text"
        placeholder="Enter Recipe Name"
